async function fetchHtml(){try{const response=await fetch('./html/bio.html'),htmlContent=await response.text();return htmlContent}catch(error){console.error('Error loading HTML:',error)}}fetchHtml().then(htmlContent=>{document.getElementById('bio-content').innerHTML=htmlContent;const furtherTag=document.getElementById('span_further');if(!Modernizr.touch){const skrollrCss=document.createElement('link');skrollrCss.type='text/css';skrollrCss.rel='stylesheet';skrollrCss.href='css/style_skrollr.css';document.head.appendChild(skrollrCss);skrollr.init();const bioTag=document.getElementById('bio');bioTag.setAttribute('data-scroll-index','2');furtherTag.setAttribute('data-scroll-goto','4');$.scrollIt({topOffset:-1})}else{const touchCss=document.createElement('link');touchCss.type='text/css';touchCss.rel='stylesheet';touchCss.href='css/style_touch.css';document.head.appendChild(touchCss);const bioTag=document.getElementById('bio-content');bioTag.setAttribute('data-scroll-index','2');furtherTag.setAttribute('data-scroll-goto','3');$.scrollIt({topOffset:-55})}});$(document).ready(function(){$(".animsition").animsition({inClass:'fade-in',outClass:'fade-out',inDuration:1500,outDuration:800,linkElement:'.animsition-link',loading:true,loadingParentElement:'body',loadingClass:'animsition-loading',loadingInner:'',timeout:true,timeoutCountdown:2500,onLoadEvent:true,browser:['animation-duration','-webkit-animation-duration'],overlay:false,overlayClass:'animsition-overlay-slide',overlayParentElement:'body',transition:function(url){window.location.href=url}})});
