(function(){const menuButton=document.getElementById('open-button'),menuIcon=menuButton.querySelector('.material-icons'),iconList=document.querySelector('.icon-list'),logo=document.getElementById('logo').getElementsByTagName('a')[0],bioSection=document.getElementById('bio');var isOpen=false;function toggleMenu(){if(isOpen){document.body.classList.remove('show-menu')}else{document.body.classList.add('show-menu')}isOpen=!isOpen};function changeIcon(){if(menuIcon.textContent==='menu'){menuIcon.textContent='close';menuIcon.setAttribute('aria-label','Close')}else{menuIcon.textContent='menu';menuIcon.setAttribute('aria-label','Menu')}};function srollTuning(){const bioTop=bioSection.offsetTop,bioBottom=bioTop+bioSection.offsetHeight,scrollPosition=window.scrollY||document.documentElement.scrollTop;if(scrollPosition>=bioTop&&scrollPosition<=bioBottom){menuButton.classList.remove('default_top');logo.classList.remove('default_top');menuButton.classList.add('tuned_top');logo.classList.add('tuned_top')}else{menuButton.classList.remove('tuned_top');logo.classList.remove('tuned_top');menuButton.classList.add('default_top');logo.classList.add('default_top')};if(scrollPosition>=bioBottom){logo.style.display="none"}else{logo.style.display=""}};function initEvents(){menuButton.addEventListener('click',toggleMenu);menuButton.addEventListener('click',changeIcon);iconList.addEventListener('click',toggleMenu);iconList.addEventListener('click',changeIcon);menuButton.addEventListener('mouseenter',function(){this.classList.add('hovered')});logo.addEventListener('mouseenter',function(){this.classList.add('hovered')});menuButton.addEventListener('mouseleave',function(){this.classList.remove('hovered')});logo.addEventListener('mouseleave',function(){this.classList.remove('hovered')});window.addEventListener('scroll',srollTuning)};function landingDefault(){menuButton.classList.add('default_top');logo.classList.add('default_top')};landingDefault();initEvents()})();
